import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import Creators from 'Redux/product'
import { removeVI } from 'jsrmvi'

//
import { Table, Row, Col, Button, Tag, Image, Typography } from 'antd'
import { LinkOutlined, PlusCircleOutlined, DownloadOutlined } from '@ant-design/icons'

const columns = [
  {
    title: '',
    dataIndex: 'avatar',
    key: 'avatar',
    render: (value, record) => {
      return (
        <img 
          src={(value && value[0]) || "data:image/png;base64,"}
          style={{ width: 30, height: 30}}
        />
      )
    },
  },
  {
    title: <div>Sản phẩm</div>,
    dataIndex: 'name',
    key: 'name',
    render: (text, record) => {
      console.log(record)
      return <>
      <Link to={`/app/product/${record.parentId}/variant/${record._id}`}>{record.name}</Link><br/>
      {record.store_id}
    </>
    },
  },
  {
    title: <div>Mã sku</div>,
    dataIndex: 'sku',
    key: 'sku',
  },
  {
    title: <div>Có thể bán</div>,
    dataIndex: 'onHand',
    key: 'onHand',
    render: (value, record) => <span>{record?.inventories?.onHand || 0}</span>
  },
  {
    title: <div>Đang giao dịch</div>,
    dataIndex: 'onHand',
    key: 'onHand',
    render: (value, record) => <span>{record?.inventories?.trading || 0}</span>
  },
  {
    title: <div>Hàng sắp về</div>,
    dataIndex: 'incoming',
    key: 'incoming',
    render: (value, record) => <span>{record?.inventories?.incoming || 0}</span>
  },
  {
    title: <div>Giá bán lẻ</div>,
    dataIndex: 'retailPrice',
    key: 'retailPrice',
    render: (value, record) => <span>{value}</span>
  },
]

export const AllProductTab = (props) => {
  const [dataSource, setDataSource] = useState([])
  useEffect(() => {
    props.getProductsStart()
  }, [])

  useEffect(() => {
    if(props.products.length > 0) {
      const allVariants = props.products.reduce((acc, product) => acc = [...acc, ...product.variants.map(va => ({ ...va, parentId: product._id}))], [])
      setDataSource(allVariants)
    }
  }, [props.products])

  return (
    <Row
      style={{ 
        backgroundColor: '#fff',
        padding: '8px 24px',
        border: '1px solid #ccc',
        borderRadius: 5
      }}
    >
      <Col span={24}>
        <Table 
          bordered
          dataSource={dataSource} 
          columns={columns} 
          hideOnSinglePage={true}
          pagination={{
            pageSize: 10,
            total: dataSource?.length || 0
          }}
        />
      </Col>
    </Row>
  )
}

const mapStateToProps = (state) => ({
  products: state.product.toJS().products
})

const mapDispatchToProps = dispatch => ({
  getProductsStart: () => dispatch(Creators.getProductsStart())
})

export default connect(mapStateToProps, mapDispatchToProps)(AllProductTab)
